package com.example.demo.controllers;

import java.security.Principal;
import java.util.List;

import org.apache.commons.codec.binary.Base64;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.MediaType;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.security.core.Authentication;
import org.springframework.security.oauth2.common.exceptions.UnauthorizedUserException;
import org.springframework.security.oauth2.provider.OAuth2Authentication;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import com.example.demo.models.User;
import com.example.demo.models.UserService;
import com.example.demo.security.AuthUser;

@RestController

public class UserController {

	@Autowired
	private UserService userService;

	@PostMapping(value = "/user")
	public User addUser(@ModelAttribute User user) throws Exception {
		return this.userService.saveUser(user);
	}
	
	
	@GetMapping("/users")
	@PreAuthorize("hasAuthority('ADMIN')")
	public List<User> getAllUsers() {
		List<User> users = this.userService.getAll();

		return users;
	}
	
	@RequestMapping("/userinfo")
	public User user(Principal principal) {
		if (principal != null) {
			OAuth2Authentication oAuth2Authentication = (OAuth2Authentication) principal;
			Authentication authentication = oAuth2Authentication.getUserAuthentication();
		
			if(authentication == null) {
				throw new UnauthorizedUserException("Unauthorized access");
			}
			
			AuthUser authUser = (AuthUser)authentication.getPrincipal();
			return authUser.getUser();
		}
		return null;
	}
	
	
	@GetMapping(value="/users/images/{id}", produces = MediaType.IMAGE_PNG_VALUE)
	public byte[] getUserImage(String id) {
		
		String image = "";
		byte[] bytes =  Base64.decodeBase64(image);
		return bytes;
	}
}